
public class ConditionalTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int person1 = 18;
		int person2 = 19;
		
		
		if ((person1 == person2))
			System.out.println("person 1 and person 2 are the same age");
		if ((person1 != person2))
			System.out.println("person 1 and person 2 are not the same age");
		if ((person1 > person2))
			System.out.println("person 1 is older");
		if ((person1 < person2))
			System.out.println("person 1 is younger");
		
	}

}
