
public class ForArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char[] alpha = 
			{'a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p','q','r','s','t','u','v','w','x','y','z'};
			

		for (int item: alpha) {
			System.out.printf("The alphabet is: %c \n",item);
		}

}}
